import React, {useEffect, useState} from "react";
import axios from 'axios';
import Row from "react-bootstrap/Row";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import CardDeck from "react-bootstrap/CardDeck";
import Container from "react-bootstrap/Container";
import {Link} from "react-router-dom";
import {Image} from "react-bootstrap";


function IndexPage(){

    const [posts, setPosts] = useState([])

    useEffect(() => {
        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/posts-index/`
        }).then(response => {
            setPosts(response.data)
        })
    }, [])

    return (
        <Container>
            <Row>
            <Col sm={10}>
                <h3 className="pb-4 mb-4 fst-italic border-bottom">
                    Top News
                </h3>
                <article className="blog-post">
                    <Row xs={2} md={2}>
                    {posts.map(p => (
                        <Col md-2>
                            <div style={{"height":"100px"}}>
                                <div className="card mb-3" style="max-width: 120px;">
                                <div className="row g-0">
                                    <div className="col-md-4">
                                        <img src={p.image} alt="..."/>
                                    </div>
                                    <div className="col-md-8">
                                        <div className="card-body">
                                            <h5 className="card-title">Card title</h5>
                                            <p className="card-text">This is a wider card with supporting text below as
                                                a natural lead-in to additional content. This content is a little bit
                                                longer.</p>
                                            <p className="card-text"><small className="text-muted">Last updated 3 mins
                                                ago</small></p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            </div>
                        </Col>
                        ))}
                </Row>
                </article>
            </Col>
            <Col sm={4}>
                <br/>
                <br/>
                <br/>
                <div className="p-4 mb-3 bg-light rounded">
                    <h4 className="fst-italic">About</h4>
                    <p className="mb-0">Customize this section to tell your visitors a little bit about your publication, writers, content, or something else entirely. Totally up to you.</p>
                </div>
            </Col>
            </Row>
        </Container>
    )
}

export default IndexPage;
