import React, {useEffect, useState} from "react";
import axios from 'axios';
import { Link } from 'react-router-dom';


function CategoryDetail({ match }) {

    const [category, setCategory] = useState({})
    const [posts, setPosts] = useState([])
    const id = match.params.id


    useEffect(() => {
        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/category/${id}/`
        }).then(response => {
            setCategory(response.data)
            setPosts(response.data.posts)
        })
    }, [id])

    return (
        <div>
            <h1>{category.name}</h1>
            <hr/>
            <div className="row">
                {posts.map(p => (
                    <div className="card mb-3" style={{"max-width:": "270px"}} key={p.id}>
                        <div className="row g-0">
                            <div className="col-md-2">
                                <img src={p.image} alt={p.title} className="figure-img img-fluid rounded"/>
                            </div>
                            <div className="col-md-8">
                                <div className="card-body">
                                    <h5 className="card-title">{p.title}</h5>
                                    <p className="card-text">{p.content}</p>
                                    <p className="card-text"><small className="text-muted">{p.pub_date}</small></p>
                                    <Link to={{ pathname: `/posts/${p.id}`, fromDashboard: false}}>Детали</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
       </div>
    )
}
export default CategoryDetail;
