import React, { useState, useEffect } from "react";
import axios from 'axios';
import Carousel from 'react-bootstrap/Carousel'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'


function PostDetail({ match }){

    const [post, setPost] = useState({})
    const id = match.params.id
    const [images, setImage] = useState([])

    useEffect(() => {
        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/blogpost/${id}/`
        }).then(response => {
            setPost(response.data)
            setImage(response.data.images)
        })
    }, [id])

    return (
        <Container fluid>
            <Row>
                <hr/>
                <Col >
                    <Row>
                        <img src={post.image} className="img-fluid" alt="Responsive image" role="listbox"  style={{ "width":"100%", "height": "250px"}}/>
                    </Row>
                    <Row>
                        <h1>{post.title}</h1>
                        <div className="card">
                            <div className="card-body">
                                {post.content}
                            </div>
                        </div>
                    </Row>
                </Col>
                <Col>
                    <h1>{post.title}</h1>
                </Col>

        </Row>
            <Row>
                <Carousel className="carousel-inner" role="listbox"  style={{ "width":"100%", "height": "500px"}}>
                                {images.map(p=>(
                                <Carousel.Item>
                                    <img src={p.image} className="d-block w-100"/>
                                    <Carousel.Caption>
                                        <p>{post.title}</p>
                                    </Carousel.Caption>
                                </Carousel.Item>
                                ))}
                        </Carousel>
            </Row>
</Container>
    )
}

export default PostDetail;
