import React, {useEffect, useState} from "react";
import axios from 'axios';
import Row from "react-bootstrap/Row";
import Card from "react-bootstrap/Card";
import Col from "react-bootstrap/Col";
import CardDeck from "react-bootstrap/CardDeck";
import Container from "react-bootstrap/Container";
import {Link} from "react-router-dom";


function IndexPage(){

    const [posts, setPosts] = useState([])

    useEffect(() => {
        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/posts-index/`
        }).then(response => {
            setPosts(response.data)
        })
    }, [])

    return (
        <Container>
            <Row>
            <Col sm={10}>
                <h3 className="pb-4 mb-4 fst-italic border-bottom">
                    Top News
                </h3>
                <article className="blog-post">
                    <Row xs={2} md={2}>
                    {posts.map(p => (
                        <Col md-2>
                            <div className="row g-0 border rounded overflow-hidden flex-md-row mb-4 shadow-sm h-md-250 position-relative">
                                <Col sm={3}>
                                    <strong className="d-inline-block mb-2 text-primary">{p.blog_category}</strong>
                                    <h3>{p.title}</h3>
                                    <div className="mb-1 text-muted">Nov 12</div>
                                    <p className="card-text mb-auto">{p.content}</p>
                                    <Link to={{ pathname: `/posts/${p.id}/`, fromDashboard: false }}>Read more</Link>
                                </Col>
                                <Col className="col-auto d-none d-lg-block">

                                </Col>
                            </div>

                        </Col>
                        ))}
                </Row>
                </article>
            </Col>
            <Col sm={4}>
                <br/>
                <br/>
                <br/>
                <div className="p-4 mb-3 bg-light rounded">
                    <h4 className="fst-italic">About</h4>
                    <p className="mb-0">Customize this section to tell your visitors a little bit about your publication, writers, content, or something else entirely. Totally up to you.</p>
                </div>
            </Col>
            </Row>
        </Container>
    )
}

export default IndexPage;
