import React, {useEffect, useState} from "react";
import axios from 'axios';
import { Link } from 'react-router-dom';
import {brown} from "@material-ui/core/colors";
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import NavLink from 'react-bootstrap/NavLink'
import Image from 'react-bootstrap/Image'


function CategoryDetail({ match }) {

    const [category, setCategory] = useState({})
    const [posts, setPosts] = useState([])
    const id = match.params.id


    useEffect(() => {
        axios({
            method: "GET",
            url: `http://127.0.0.1:8000/api/category/${id}/`
        }).then(response => {
            setCategory(response.data)
            setPosts(response.data.posts)
        })
    }, [id])

    return (
            <Container>
                <br/>
                <Row>
                    <Image src="http//:127.0.0.1:8000/media/04ddf67f8e-2_1390x600_NztD8G8.jpg" fluid />
                    <div className="row g-2">
                        {posts.map(p => (
                        <div className="col-6">
                            <div className="card mb-3" style={{"max-width":"540px"}}>
                                <div className="row g-0">
                                    <div className="col-md-4">
                                        <img className="card-img-top" src={p.image} alt={p.title}/>
                                    </div>
                                    <div className="col-md-8">
                                        <div className="card-body">
                                            <h5 className="card-title">{p.title}</h5>
                                            <p className="card-text">{p.content}</p>
                                            <p className="card-text"><small className="text-muted">{p.pub_date}</small></p>
                                            <Link className="nav-link" to={{ pathname: `/posts/${p.id}/`, fromDashboard: false }}>Learn more</Link>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        ))}
                    </div>
                </Row>
            </Container>

    )
}
export default CategoryDetail;
